node.warn({debug: "Building Database", config});
MOONSHOTS.ORAMA.upsert = async (db, rec, key) => { 
  const keyField = key || 'id';
  const keyValue = rec[keyField];

  // No key value
  if (!keyValue) { return await Orama.insert(db, rec); }
  let search = { term: keyValue, where: { [keyField]: keyValue }, limit: 1 }

  // No existing results
  const res = await Orama.search(db, search);
  if (res.count == 0) { return await Orama.insert(db, rec); }

  // Merge
  const existing = res.hits[0].document;
  const merged = { ...existing, ...rec, [keyField]: keyValue };

         await Orama.remove(db, res.hits[0].id)
  return await Orama.insert(db, merged);

};

msg.orama.results = MOONSHOTS.ORAMA[config.database] = await Orama.create(config.payload);

global.set('__MOONSHOTS__', MOONSHOTS)
return msg;

let MOONSHOTS = global.get('__MOONSHOTS__') || {}

let db = msg.orama = msg.orama || {}
db.config  = {}

db.config.database =  MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-database"))
if (!db.config.database || db.config.database == "env-database") return [msg, undefined];

db.config.query =  MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-query"))
if (!db.config.query || db.config.query == "env-query") return [msg, undefined];

db.config.payload =  MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-payload"))
node.warn(db.config.payload)
if (!db.config.payload || db.config.payload == "env-payload") delete db.config.payload;

db.config.recId =  MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-rec-id"))
if (!db.config.recId || db.config.recId == "env-rec-id") delete db.config.recId;

db.results = {}
return [undefined, msg];

let MOONSHOTS = global.get('__MOONSHOTS__') || {}
if (MOONSHOTS.ORAMA == undefined) { MOONSHOTS.ORAMA = {}}

let config = msg.orama.config
let db = MOONSHOTS.ORAMA[config.database]
if (!db){ node.warn("Missing Database " + config.database); return msg }
if (!config.payload){ node.warn('Missing payload'); return msg }

// Single record
if (!Array.isArray(config.payload)){
    msg.orama.results = await MOONSHOTS.ORAMA.upsert(db, config.payload, config.recId);
    global.set('__MOONSHOTS__', MOONSHOTS)
    return msg;
}

// Batch insert
if (!config.recId) {
    msg.orama.results = await Orama.insertMultiple(db, config.payload);
    global.set('__MOONSHOTS__', MOONSHOTS)
    return msg;
}

// Loop upsert
let results = msg.orama.results = []
for (let rec of config.payload){
    results.push(await MOONSHOTS.ORAMA.upsert(db, rec, config.recId));
}
global.set('__MOONSHOTS__', MOONSHOTS)
return msg;

let MOONSHOTS = global.get('__MOONSHOTS__') || {}
if (MOONSHOTS.ORAMA == undefined) { MOONSHOTS.ORAMA = {}}

let config = msg.orama.config
let db = MOONSHOTS.ORAMA[config.database]
if (!db){ node.warn("Missing Database " + config.database); return msg }
if (!config.payload){ node.warn('Missing payload'); return msg }

// Single record
if (typeof config.payload == 'string'){
    msg.orama.results = await Orama.remove(db, config.payload)
}

// Record is an object
else if (!Array.isArray(config.payload)){
    let recId = config.payload['id' || config.recId] 
    msg.orama.results = await Orama.remove(db, recId)
}

// An Array of String (record Ids)
else if (!config.recId) {
    msg.orama.results = await Orama.removeMultiple(db, config.payload);
}

// An Array of records with custom Id
else {
    let results = msg.orama.results = []
    for (let rec of config.payload){
        results.push(await Orama.remove(db, rec[config.recId]))
    }
}

global.set('__MOONSHOTS__', MOONSHOTS)
return msg;

