let MOONSHOTS = global.get('__MOONSHOTS__') || {}

// Retrieve Audio Buffer
msg.payload = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-audio-buffer")) || msg.payload

let subskey      = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-speech-key"))
let subscription = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-speech-config"))

// Speech Config : https://learn.microsoft.com/en-us/azure/ai-services/speech-service/speech-services-private-link?tabs=portal
let speechConfig = SpeechSDK.SpeechConfig.fromSubscription(subskey, subscription.region);

// Build Speech Recognizer
let autoDetectSourceLanguageConfig = SpeechSDK.AutoDetectSourceLanguageConfig.fromLanguages(["en-US", "fr-FR"]);
let audioConfig = SpeechSDK.AudioConfig.fromWavFileInput(msg.payload);

let speechRecognizer = SpeechSDK.SpeechRecognizer.FromConfig(speechConfig, autoDetectSourceLanguageConfig, audioConfig);
speechRecognizer.recognizeOnceAsync((result) => {
        let languageDetectionResult = SpeechSDK.AutoDetectSourceLanguageResult.fromResult(result);
        let detectedLanguage = languageDetectionResult.language;
        msg.payload = { result, detectedLanguage, DisplayText : result.text }
        speechRecognizer.close();
        node.send([undefined, msg]);
},(err) => {
    speechRecognizer.close();
    msg.payload = {fill : 'red', 'text' : err};
    node.send([msg, undefined]);
});

let MOONSHOTS = global.get('__MOONSHOTS__') || {}
