:root {
    --theme-color-background: #f5f5f5;
    --theme-color-foreground: #000000;

    --theme-color-primary-bg: #0889ba;
    --theme-color-primary-fg: #ffffff;
    --theme-color-primary-hl: #4be1fc;
    --theme-color-primary-btn: #04506d;

    --theme-color-secondary-bg: #303440;
    --theme-color-secondary-fg: #ffffff;
    --theme-color-secondary-hl: #69728c;

    --theme-color-chat-bg: #EEEEEE;
    --theme-bubble-radius: 1em;
}

/* ------------------------------------------ */
/*  COMMON                                    */
/* ------------------------------------------ */

BODY {
    color: var(--theme-color-foreground);
    background: var(--theme-color-background);
}

/* ------------------------------------------ */
/*  CHAT : CUSTOM                             */
/* ------------------------------------------ */

PRE {
    background: #282c34;
    padding: var(--theme-bubble-radius);
    border-radius: 0.4em;
    font-size: 12px;
    line-height: 16px;
}

.app-chat-container {
    position: absolute;
    top: 2em;
    bottom: 2em;
    left: 2em;
    right: 2em;
    border-radius: 0.4em;
    overflow: hidden;
    border: 1px solid var(--theme-color-primary-hl);
    background: var(--theme-color-chat-bg);
}

/* ------------------------------------------ */
/*  CHAT                                      */
/* ------------------------------------------ */

.app-chat-side {
    position: absolute;
    top: 2em;
    bottom: 2em;
    left: 2em;
    right: 2em;
}


#app-chat {
    position: absolute;
    left: 0;
    right: 0;
    top: 3em;
    bottom: 6em;
    overflow-y: scroll;
}

#app-chat .chat-bubble {
    margin: var(--theme-bubble-radius);
    padding: var(--theme-bubble-radius);
    border-radius: var(--theme-bubble-radius);
}

.chat-bubble H1,
.chat-bubble H2,
.chat-bubble H3,
.chat-bubble H4,
.chat-bubble H5,
.chat-bubble H6 {
    font-size: 1.1rem;
}

.chat-bubble IMG {
    max-width: 100%;
    border: 1px solid var(--theme-color-primary-fg);
}

.chat-bubble A {
    color: var(--theme-color-primary-hl);
}

#app-chat .chat-bubble P:last-child {
    margin-bottom: 0;
}

#app-chat .chat-bubble-app {
    color: var(--theme-color-primary-fg);
    background: var(--theme-color-primary-bg);
    margin-left: calc(var(--theme-bubble-radius)*4);
    margin-right: 10%;
}

#app-chat .chat-bubble-user {
    color: var(--theme-color-secondary-fg);
    background: var(--theme-color-secondary-bg);
    margin-left: 30%;
}

#app-chat .chat-bubble-sys {
    color: var(--theme-color-primary-fg);
    background: var(--theme-color-primary-bg);
}

.chat-bubble-sys::before {
    content: 'âŒ›';
    position: relative;
    float: left;
}

/* ------------------------------------------ */
/*  CHAT : HEADER                             */
/* ------------------------------------------ */

#app-chat-header {
    position: absolute;
    left: 0;
    right: 0;
    top: 0;
    height: 3em;
    color: var(--theme-color-primary-fg);
    background: var(--theme-color-primary-bg);
    text-align: left;
    font-weight: bold;
    padding: 0.4em;
    border-bottom: 1px solid var(--theme-color-primary-hl);
}

#app-chat-title {
    position: absolute;
    left: 0.6em;
    top: 0.6em;
}

#app-status {
    position: absolute;
    right: 0;
    width: 40%;
    top: 0.6em;
    right: 0.6em;
    text-align: right;
    font-weight: normal;
    color: var(--theme-color-primary-hl);
}

/* ------------------------------------------ */
/*  CHAT : FOOTER                             */
/* ------------------------------------------ */

#app-chat-footer {
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    height: 6.2em;
    border-top: 1px solid var(--theme-color-primary-hl);
    color: var(--theme-color-primary-fg);
    background: var(--theme-color-primary-bg);
    padding: 0.4em;
    padding-right: 10%;
}

#app-chat-footer .footer-btn {
    position: absolute;
    right: 0.4em;
    top: 0.4em;
    width: 8%;
}

#app-chat-footer .footer-btn .btn {
    border: 1px solid var(--theme-color-primary-hl);
    background: var(--theme-color-primary-btn);
    color: var(--theme-color-primary-fg);
    margin-bottom: 0.4em;
    width: 100%;
}

#app-chat-footer #chat-message {
    background: var(--theme-color-chat-bg);
}

!function ($) {
  $.CLIENT = $.CLIENT || {};
  $.CLIENT.CONFIG =  $.CLIENT.CONFIG || {}
  $.CLIENT.CONFIG.webchat = {}

  // ------------------------------------------
  //  PUBLIC
  // ------------------------------------------

  let interrupt = undefined;
  const handleChat = $.CLIENT.handleChat = (msg) => {
    let chat = msg.message.event.value
    console.log(chat)
    // Prevent multiple call, but it doesn't fix Same Ack Call
    if (interrupt){ interrupt(); }

    // Prepare callback
    let callback = interrupt = () => {
        interrupt = undefined;
        //$.CLIENT.stopSpeech(); // too much ?

        // Send Ack after animation
        $.CLIENT.sendAck(msg.replyId);
    }

    // Create/Add chat bubble
    setChatBubble(chat, () => { /* nothing  */ })

    // An echo from the user
    if (chat.from === 'user')   { return callback(); }
    if (chat.from === 'system') { return callback(); }

    // Display button advice first
    //if (chat.buttons && chat.text){ $.CLIENT.showAdvice(chat.text)}

    // Then speech
    if (!$.CLIENT.CONFIG.webchat.tts){ return callback(); }
    $.CLIENT.textToSpeech(chat.speech || chat.text || chat, () => {
        return callback();
    });
  }

    // ------------------------------------------
    //  PRIVATE
    // ------------------------------------------

    const addBubble = (message, callback) => {
        let text = message
        let type = 'chat-bubble-app'
        let buttons = '';

        if (typeof message !== 'string'){
            text = message.text

            if (marked){
                text = marked.parse(text)
            }

            // Add Bubble User
            if (message.from === 'user'){
                type = 'chat-bubble-user'
            }

            // Is System message
            if (message.from === 'system'){
                type += ' chat-bubble-sys'
            }

            // Add Bubble Buttons
            if (message.buttons){
                buttons += '<div class="chat-bubble-btn">'
                for (let btn of message.buttons){
                    buttons += '<button value="' + (btn.value || btn.title ) + '">'+ btn.title +'</button>'
                }
                buttons += '</div>'
            }
        }

        // Create Bubble
        let bubble  = '<div class="chat-bubble ' + type + ' bounceInLeft animated">'
            bubble += '<div class="chat-bubble-content">' + text + buttons + '</div>'
            bubble += '</div>'
        let $bubble = $(bubble);

        // Highlight the code
        $bubble.find('pre code').each(function(i, block) {
            let options = { language : 'Markdown'} // default on Markdown
            if (block.className && block.className.startsWith('language')){
                options = { language : block.className.substring(9) }
            }
            let highlighted = hljs.highlight(block.textContent, options);
            block.innerHTML = highlighted.value
        });

        // Add Bubble to Chat
        $chat = $( "#app-chat" )
        $chat.find('.chat-bubble-sys').remove();
        $chat.append($bubble);


        // Animate
        if (message.from !== 'system'){
            $chat.animate({ scrollTop: $chat.prop("scrollHeight")}, 1000);

            // Callback after bounce
            setTimeout(callback, $.CLIENT.CONFIG.animate.bounce)

        } else { callback(); }


    }

  let _handle;
  const setChatBubble = (message, callback) => {

      // Reset cleaning
      clearTimeout(_handle);

      // Clean too many bubble
      let bubbles = $( "#app-chat .chat-bubble")
      addBubble(message, callback);
  }

  // ------------------------------------------
  //  REGISTER
  // ------------------------------------------

  const register = () => {
    $('#chat-send').on('click', () => {
        let message = $('#chat-message').val();
        if (!message) return;
        $.CLIENT.send("ws:chat", message);
        $('#chat-message').val('');
    })

    $('#chat-speech').on('click', () => {
        let state = !$.CLIENT.CONFIG.webchat.tts
        $.CLIENT.CONFIG.webchat.tts = state;
        $('#chat-speech').html(state ? 'ðŸ”Š' : 'ðŸ”‡');

    })

    $('.chat-action').on('click', (e) => {
        let message = $(e.target).text();
        if (!message) return;
        $.CLIENT.send("ws:chat", message);
    })

    $.CLIENT.listenEvent('ws:chat', handleChat);
  }

  // Initialization on DOM ready
  $(() => { register(); });

}(jQuery);

