let MOONSHOTS = global.get('__MOONSHOTS__') || {}

let scrap = {}
scrap.mode   = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-scrap"))
scrap.html   = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-html"))

if (scrap.mode == 'custom'){
    scrap.custom = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-custom"))
}

if (scrap.html){
    msg.scrap = scrap
    return [undefined, undefined, msg]
}

scrap.url = msg.url = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-url"))
if (!scrap.url){
    return [msg, undefined, undefined]
}

scrap.header = msg.headers = {
    'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/115.0.0.0 Safari/537.36 Alambic/1.0',
    'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
    'Accept-Language': 'fr-FR,fr;q=0.9,en-US;q=0.8,en;q=0.7'
}

let headers = MOONSHOTS.UTIL.getMessageProperty(msg, env.get("env-headers"))
if (headers !== null && typeof headers === 'object'){
    Object.assign(scrap.header, headers);
}

msg.scrap = scrap
return [undefined, msg, undefined]

// Check the cleanning process
if (!msg.scrap.mode || msg.scrap.mode == 'raw'){
    msg.scrap.result = msg.scrap.html
    return msg;
}

const $ = cheerio.load(msg.scrap.html);

// Find the relevent content if any
let mainContent = $('article, main, #content, .content, .page-content, .entry-content, .main-content, .post').toString();
if (mainContent) {  $('body').html(mainContent); }

// ------------------------------------------
//  MAIN content
// ------------------------------------------

if (msg.scrap.mode == 'main') { 
  let maxLength = 0;
  // Try to find the main content
  $('article, .content, .page-content, .entry-content, .main-content, .post').each((index, element) => {
    let $e = $(element);
    let textContent = $e.text().trim()
    if (textContent.length > maxLength) {
      maxLength = textContent.length;
      mainContent = $e.toString();
    }
  });
  if (mainContent) { $('body').html(mainContent);  }
}

// ------------------------------------------
//  CUSTOM content
// ------------------------------------------

if (msg.scrap.mode == 'custom' && msg.scrap.custom){
  $(msg.scrap.custom).each((index, element) => {
    let $e = $(element);
    mainContent += $e.toString();
  })
  if (mainContent) { $('body').html(mainContent);  }
}

// ------------------------------------------
//  CLEAN content
// ------------------------------------------

// Remove all script and unnecessary elements
$('header, nav, footer, aside, style, script, noscript, link, iframe, svg, form, button, input, select, textarea, .advertisement, .ad, .banner, .widget, .promo, .popup').remove();


// Remove very long lists
$('ul, ol').each((index, list) => {
  if ($(list).find('li').length > 8) { $(list).remove(); }
});

// Remove all aside, nav, section, div with more than 5 links
let clean_links = 'aside, nav, section, div, ol, P'
if (msg.scrap.mode == 'custom' || msg.scrap.mode == 'clean-links'){
  clean_links = 'aside, nav, section, ol'
}

  let cache = {};
  $('a').each((index, element) => {
    let $e = $(element);
    let parent = $e.closest(clean_links);
    let parentHtml = parent.html();

    if (!cache[parentHtml]) {
      cache[parentHtml] = { count: 0, element: parent };
    }

    cache[parentHtml].count++;
  });

  for (let key in cache) {
    let data = cache[key];
    if (data.count > 4) {
      data.element.remove();
    }
  }


// Remove all sections with too many links or related content
$('aside, nav, section, div').each((index, element) => {
  let $e = $(element);

  const classes = $e.attr('class');
  if (classes && classes.indexOf('related') !== -1)      { return $e.remove(); }
  if (classes && classes.indexOf('social-share') !== -1) { return $e.remove(); }
  if (classes && classes.indexOf('signup') !== -1)       { return $e.remove(); }

  const id = $e.attr('id');
  if (id && id.indexOf('related') !== -1) { return $e.remove(); }
});


// Flatten nested divs and spans
$('div').each(function () { $(this).replaceWith($(this).contents()); });
$('span').each(function () { $(this).replaceWith($(this).contents()); });

// Comments
//$.root().find('*').addBack().contents().each((index, element) => {
//    if (element.type === 'comment') { return $(element).remove(); }
//})

$('*').each((index, element) => {
  let $e = $(element);
  // Remove empty tags
  if (!$e.html().trim()) { 
    let filter = true; let tagName = element.tagName.toLowerCase()
    if (tagName == 'img')  filter = false;
    if (tagName == 'meta' && element.attribs['name'] == 'description') filter = false;
    if (tagName == 'body') filter = false;
    if (filter){ return $e.remove();  }
  }

  // Remove specific attributes
  Object.keys(element.attribs).forEach(attr => {
    if (attr.startsWith('id') || attr.startsWith('class') || attr.startsWith('style') || attr.startsWith('data-') || attr.startsWith('aria-') || attr.startsWith('lang') || attr.startsWith('dir') || attr.startsWith('prefix')) {
      $e.removeAttr(attr);
    }
  });
});

// Remove white space, etc ...
let content = $.html();
content = content.replace(/<!--[\s\S]*?-->/g, ''); // HTML comments
content = content.replace(/[ \t]+/g, ' ');
content = content.replace(/^\s+/gm, '');
content = content.replace(/(\r\n|\n|\r)/g, '');

msg.scrap.result = content;
return msg;

node.warn(msg)
msg.scrap.html = msg.payload
return [undefined, msg];

let t0 = Date.now()
let model = msg.llm.model

try { 
    let options    =  Object.assign({}, msg.llm.dalle.options); 
    options.model  = ''
    options.prompt  = msg.llm.dalle.data
    let result = await model.client.images.generate(options);
