    return node.send([msg, undefined], false);
}

// Debug duration of API calls
msg.llm.debug.duration = (Date.now() - t0); 


// Handle result assume there is options.count images
let datas = msg.llm.result.data
if (!datas){
    msg.llm.error = { err : 'No LLM images' }
    return node.send([msg, undefined],false);
}

return node.send([undefined,msg],false);

let MOONSHOTS = global.get('__MOONSHOTS__') || {}

msg.llm = msg.llm || {}
