const sendTool = (message, idx) => {
    // End of Loop
    if (idx >= message.tool_calls.length){
        return node.send([undefined, msg], false)
    }

    // Sometimes LLM provide a message along tool call to explain what it do
    if (message.content){
        let response = message.content
        try { 
            response = JSON.parse(response) } 
        catch (ex) {
            response = { text: response } // We ask for a JSON object
        }
        msg.llm.tool_pre_response = response
    }

    // Store the current Tool
    msg.llm.tool = message.tool_calls[idx]

    // Build callback
    msg.llm.toolCallback = function(content, context_Id){
        delete msg.llm.tool_pre_response // clean
        node.warn({info: "toolCallback", content, context_Id, llm: msg.llm})
        msg.llm.toolCalls.push({ 
            "role": "tool", 
            "content": JSON.stringify(content), 
            "tool_call_id": msg.llm.tool.id 
        })

