        if (context_Id){
            msg.llm.prompt.context       = msg.llm.prompt.context || {}
            msg.llm.prompt.context.tools = msg.llm.prompt.context.tools || {} 
            if (context_Id === true) { context_Id = 'tool_' + msg.llm.tool.id + '_' + Date.now() } // Will cumulate tools results
            msg.llm.prompt.context.tools[context_Id] = content  // Will override tools result
        }

        return sendTool(message, idx+1)
    }

    // Debug Tool
    node.warn({debug: 'Debug Tool', tool: msg.llm.tool})

    // LLM_Tool_INTERNAL_Image
    if (msg.llm.tool['function'].name == "LLM_Tool_INTERNAL_Image"){
        let tool = msg.llm.tool
        let args = JSON.parse(tool['function'].arguments);
        return msg.llm.toolCallback({"LLM_Tool_INTERNAL_Image" : args.image});
    }

    // Forward to current tool
    return node.send([msg, undefined], false)
}

