const pushToHistory = (content, override) => {
    let history = msg.llm.prompt.history = msg.llm.prompt.history || []
    if (history.length == 0) { return history.push({ role: "assistant", content: content }) }

    let lastHist = history[history.length - 1] // First time we answer
    if (lastHist.role !== "assistant") { return history.push({ role: "assistant", content: content }) }

    // Cumulative answer for streaming unless it's JSON's text
    lastHist.content = override ? content : (lastHist.content + content) 
