*/
    // Manually process the stream using the async iterator's next() method
    const iterator = stream[Symbol.asyncIterator]();

    async function processStream() { 
      let result; let toolCalls = []
      do {
        result = await iterator.next();
        if (!result.done) {
          const chunk = result.value; //node.warn(chunk);
          msg.llm.result = chunk // send all the chuncks

          // Yes It could happened
          if (!chunk || chunk.choices?.length == 0) continue;

          // ------------------------------------------
          //  Handle chunks of content or tools
          // ------------------------------------------

          // Working on chunk content
          if (chunk?.choices?.[0]?.delta?.content) {
            //msg.llm.stream.chunks.push(chunk) // keep track
            msg.llm.stream.content += chunk?.choices?.[0]?.delta?.content // cumulate content
          }

          // Working on chunk tools
          let chunkToolCalls = chunk?.choices?.[0]?.delta?.tool_calls
          if (chunkToolCalls){
            for (let chunkTool of chunkToolCalls) {
              if (chunkTool.index < toolCalls.length && toolCalls[chunkTool.index]){
                toolCalls[chunkTool.index].function.arguments += chunkTool.function.arguments
              } else {
                toolCalls.push(chunkTool)
              }
            }
            continue; // We can't do anything with it
          }

          // ------------------------------------------
          //  Consolidate Chunks
          // ------------------------------------------

          if (chunk?.choices?.[0]?.finish_reason == "stop"){
            msg.llm.result.choices[0].message = { role: "assistant", content: msg.llm.stream.content }
          }

          if (chunk?.choices?.[0]?.finish_reason == "tool_calls") { // May need to use the index
            msg.llm.result.choices[0].message = { role: "assistant", tool_calls: Object.values(toolCalls) } 
          }

          msg.llm.debug.duration = (Date.now() - t0); 
          node.send([undefined, msg]);
        }
      } while (!result.done);
    }
    await processStream();

} catch(err){ node.warn(err)
    msg.llm.error = { err };
    return node.send([msg, undefined]);
}

