!function ($) {
  $.CLIENT = $.CLIENT || {};
  $.CLIENT.CONFIG =  $.CLIENT.CONFIG || {}

  $.CLIENT.CONFIG.tts = {
    url : '{{{env.env-tts-url}}}',
  },

  $.CLIENT.CONFIG.animate = {
      bounce: 800,
      fade:   800,
  }


  // ------------------------------------------
  //  EVENT
  // ------------------------------------------

  const fireEvent = $.CLIENT.fireEvent = (name, data) => {
    let event = $.Event(name);
    event.custom = { name, data };
    $(document).trigger(event);
  }

  const listenEvent = $.CLIENT.listenEvent = (name, callback) => {
    $(document).on(name, (event) => { callback(event.custom.data) })
  }

  // ------------------------------------------
  //  STATUS
  // ------------------------------------------

  const status = $.CLIENT.status = (type, msg) => {
    console.log('Status', type, msg);
  }

  // ------------------------------------------
  //  UTIL
  // ------------------------------------------

  const getQueryVariable = (variable) => {
    var query = window.location.search.substring(1);
    var vars = query.split('&');
    for (var i = 0; i < vars.length; i++) {
      var pair = vars[i].split('=');
      if (decodeURIComponent(pair[0]) == variable) {
        return decodeURIComponent(pair[1]);
      }
    }
    console.log('Query variable %s not found', variable);
  }


  const getCookie = (name) => {
    let cookies = document.cookie.split(';');
    for(var i=0 ; i < cookies.length ; ++i) {
        var pair = cookies[i].trim().split('=');
        if(pair[0] == name){
            return pair[1] ? decodeURIComponent(pair[1]) : null;
        }
    }
    return null;
  };

  // ------------------------------------------
  //  PUBLIC
  // ------------------------------------------

  $.CLIENT.CONFIG.clientID = getQueryVariable('clientID')
  if (!$.CLIENT.CONFIG.clientID){
    var url = new URL(window.location.href);
    url.searchParams.set('clientID', 'client_' + Date.now());
    window.location.href = url.href;
  }
  console.log('New ClientID:', $.CLIENT.CONFIG.clientID)

  $.CLIENT.CONFIG.clientCookie = getCookie('session')
  const register = () => {

  }

  // Initialization on DOM ready
  $(() => { register(); });

}(jQuery);

!function ($) {

    $.CLIENT = $.CLIENT || {};

    // ------------------------------------------
    //  PUBLIC
    // ------------------------------------------

    const textToSpeech = $.CLIENT.textToSpeech = (text, callback) => {

        // No audio context
        if (audioCtxt === undefined) { return callback(); }

        // No text to speech (or hack)
        if (text === undefined || text.length <=1 || !$.CLIENT.CONFIG.tts.url ) { return callback(); }

        // Call a text to speech services
        $.CLIENT.status($.CLIENT.STATUS.NETWORK, 'Text to speech ...');
        $.CLIENT.ajax($.CLIENT.CONFIG.tts.url, { 'text': text, '_client_id' : $.CLIENT.CONFIG.clientID, 'buffer': false }, (err, result) => {
            stopAudio();

            if (err || !result){ 
                if (callback) callback(); 
                return; 
            }

            // Parse results (we ask for a JSON with buffer=false)
            let json = JSON.parse(result)
            if (!json.buffer){
                console.log('Error reading buffer', text)
                if (callback) callback(); 
                return; 
            }

            // Convert back Binary Buffer (?? work on buffer.data field)
            const binary = new ArrayBuffer(json.buffer.data.length)
            const view   = new Uint8Array(binary);
            for (let i = 0; i < json.buffer.data.length; ++i) {
                view[i] = json.buffer.data[i];
            }

             // Play audio
             playAudio(binary, callback);
             playSSML(json.ssml.viseme, 'tts:viseme')
             playSSML(json.ssml.bookmarks, 'tts:bookmarks')
        })
    }

    const stopAudio = $.CLIENT.stopSpeech = (text, callback) => {
        if (!bufferSource) return false;
        bufferSource.stop();
        bufferSource = undefined;
        return true;
    }


    let bufferSource = undefined;
    const playAudio = $.CLIENT.playAudio = (data, callback) => {
        if (audioCtxt === undefined) return callback(); // require a page gesture
        if (data === undefined) return callback(); // require an audio
        $.CLIENT.status($.CLIENT.STATUS.SPEAKER, 'Playing audio ...', undefined, 0); // Manual
        $.CLIENT.fireEvent('tts:audio:start', {})
        try {
            audioCtxt.decodeAudioData(data, function(buffer) {
                let source  = bufferSource = audioCtxt.createBufferSource();
                source.buffer = buffer;
                source.addEventListener('ended', () => { 
                    $.CLIENT.clearStatus(); 
                    $.CLIENT.fireEvent('tts:audio:end', {})
                    if (callback) callback(); 
                });
                source.connect(audioCtxt.destination);
                source.start(0);
            }, (e) => { 
                bufferSource = undefined; 
                $.CLIENT.clearStatus(); if (callback) callback(); 
            });
        } catch (ex){
            console.error(ex);
        }
    }

    const playSSML = $.CLIENT.playSSML = (ssml, event, callback) => {
        if (!ssml){ if (callback) callback(); return;}

        let timer = Date.now()
        let index = 0

        let handleTimer = () => {

            if (index >= ssml.length){  
                $.CLIENT.fireEvent(event, { index, ssml }) // notify end
                if (callback){ callback(); } 
                return; 
            }

            // Send event
            $.CLIENT.fireEvent(event, {
                index, elm : ssml[index], ssml
            })

            // Wait for next
            let offset    = Math.round(ssml[index].privAudioOffset  / 10000)
            let elapse    = Date.now() - timer
            let duration  = offset - elapse

            index += 1
            if (duration < 0){  return handleTimer(); }
            setTimeout(handleTimer, duration)
        }

        handleTimer();
    }


    // ------------------------------------------
    //  PRIVATE
    // ------------------------------------------

    let audioCtxt;
    const initAudioContext = () => { 
        if (audioCtxt !== undefined) { return audioCtxt; }
        try {
            // Fix up for prefixing
            window.AudioContext = window.AudioContext || window.webkitAudioContext;
            audioCtxt = new AudioContext();

            if (audioCtxt.state === 'suspended') {
                if (document.autoplayPolicy === 'allowed') {
                  console.log("State is suspended only because hardware is slow.");
                }
                if (document.autoplayPolicy === 'disallowed' ||
                    document.autoplayPolicy === 'allowed-muted') {
                    console.log("State is suspended because of the autoplay policy.");
                    audioCtxt.resume();
                }
            }

        } catch (err) {
            console.log("Error: ", err);
            audioCtxt = undefined;
        }

        console.log("Init Audio Context");
        return audioCtxt;
    }

    // ------------------------------------------
    //  REGISTER
    // ------------------------------------------

    const register = () => {
        $(document).on('click', initAudioContext)
        $.CLIENT.listenEvent('ws:audio', (msg) => {
            let data = msg.message.event.value
            playAudio(data, () => {
                $.CLIENT.sendAck(msg.replyId) 
            });
        });
    }

    // Initialization on DOM ready
    $(() => { register(); });

}(jQuery);

!function ($) {

    $.CLIENT = $.CLIENT || {};
    $.CLIENT.STATUS = {
        NONE       : '',
        ERROR      : 'app-status-error',
        OK         : 'app-status-ok',
        WAITING    : 'app-status-waiting',
        NETWORK    : 'app-status-network',
        MICROPHONE : 'app-status-microphone',
        SPEAKER    : 'app-status-speaker',
        CAMERA     : 'app-status-camera',
        WATCHING   : 'app-status-watching',
        HELLO      : 'app-status-hello',
        BYE        : 'app-status-bye',
    }

    // ------------------------------------------
    //  STATUS
    // ------------------------------------------

    let _status = undefined;
    let statusHandle;
    const status = $.CLIENT.status = (status, msg, params, timeout) => {

        // Print console.log() 
        console.log('Status:', msg, params || '');

        // Reset clean timer
        clearTimeout(statusHandle);

        // Backup last status
        let _backup = status + '_' + msg
        if (_backup !== _status) { // Avoid refresh animation
            _status = _backup

            // Print HTML
            let html  = '<div class="app-status-wrapper '+ status +'">';
                html += '  <div class="app-status-icon"></div>'
                html += '  <div class="app-status-text">' + msg + '</div>'
                html += '</div>'

            $('#app-status').html(html);
        }

        // Manual timeout
        if (timeout === 0){ return; }

        // Clean status after a timeout
        statusHandle = setTimeout(clearStatus, timeout || 2000);
    }

    const clearStatus = $.CLIENT.clearStatus = () => {
        $('#app-status').html('');
        _status = undefined;
    }

    // ------------------------------------------
    //  PUBLIC
    // ------------------------------------------

    const handleStatus = (msg) => {
        let text       = msg.message.event.value.text
        let params     = msg.message.event.value.params
        let status     = msg.message.event.value.status
        let status_css = $.CLIENT.STATUS[status] || status
        $.CLIENT.status(status_css, text, params)
        $.CLIENT.sendAck(msg.replyId);
    }

    const register = () => {
        $.CLIENT.listenEvent('app:status', handleStatus);
    }

    // Initialization on DOM ready
    $(() => { register(); });

}(jQuery);

!function ($) {

    $.CLIENT = $.CLIENT || {};

    // ------------------------------------------
    //  AJAX
    // ------------------------------------------

    const ajax = $.CLIENT.ajax = (url, params, callback, method, raw) => {

        let options = {
            url: url, 
            data: params,
            type: method || 'GET', 
            traditional: true
        }

        // Send blob as raw binary (without processing)
        if (raw){
            options.processData = false
            options.contentType = false
        }

        // Build Request
        let request = $.ajax(options);

        // Handle Response
        request.done((data) => {
            if (callback) callback(undefined, data);
        });

        // Handle Error
        request.fail(( jqXHR, textStatus, errorThrown ) => {
            console.error(textStatus, errorThrown);
        });
    }

    const ajaxBlob = $.CLIENT.ajaxBlob = (url, blob, callback) => {
        let data = new FormData();
        data.append("blobData", blob);
        $.CLIENT.ajax(url, data, callback, 'POST', true)
    }

    const ajaxBuffer = $.CLIENT.ajaxBuffer = (url, params, callback, method) => {

        method = method || 'GET';
        if (method === 'GET'){ 
            url += '?' + $.param(params);
        }

        let request = new XMLHttpRequest();
            request.open(method, url, true);
            request.responseType  = "arraybuffer";
            request.onload  = (e) => { callback(undefined, request.response) };
            request.onerror = ( ) => { console.error("An error occurred during the XMLHttpRequest", url) };
            request.send(method !== 'GET' ? JSON.stringify(params || {}) : undefined)
    }

    // ------------------------------------------
    //  REFRESH
    // ------------------------------------------

    const _target = (target, callback) => {
        if (!target){ return; }

        let $target = $(target);
        if ($target.length <= 0){
            location.reload();
            if (callback) callback();
            return;
        }

        $target.removeClass('fadeIn').addClass('fadeOut');
        return $target;
    }

    const updateHTML = $.CLIENT.updateHTML = (html, target, callback) => {
        let $target = _target(target, callback)
        if ($target === undefined) { return; }

        setTimeout(() => {
            $target.html(html)
            $target.removeClass('fadeOut').addClass('fadeIn');
            if (callback) callback();
        }, 500)
    }

    const refresh = $.CLIENT.refresh = (url, params, target, callback, method) => {
        let $target = _target(target, callback)
        if ($target === undefined) { return; }

        // Handle Response
        ajax(url, params, (err, html) => {
            let content = clean(html);
            if (content){

                $target.html(content.body)
                $target.removeClass('fadeOut').addClass('fadeIn');

                // Can also work on $target attributes
            }
            if (callback) callback();

            // Fire pseudo event
            let event = $.Event("ajax:ready");
            event.refresh         = {};
            event.refresh.$target = $target;
            $(document).trigger(event);

        }, method);
    }

    let clean = (html) => {

        html = html.trim();
        let patternDIV = new RegExp('^(<div[^>]*ajax-body[^>]*>)(.*)','gi');
        let mtch = patternDIV.exec(html);
        if (!mtch){ return false; }

        let $wrap = $(mtch[1]+'</div>');

        html = html.replace(patternDIV, '$2');
        html = html.substring(0, html.lastIndexOf("</div>"));
        return { 'body' : html , '$wrapper' : $wrap };
    }

    // ------------------------------------------
    //  REGISTER
    // ------------------------------------------

    const register = () => {
        $.CLIENT.listenEvent('mirror:refresh', (msg) => {
            let value = msg.message.event.value;
            let callback = (err) => { $.CLIENT.sendAck(msg.replyId); }

            if (value.html !== undefined){
                $.CLIENT.updateHTML(value.html, value.target, callback)
            } else {
                $.CLIENT.refresh(value.url, undefined, value.target, callback);
            }
        });
    }

    // Initialization on DOM ready
    $(() => { register(); });

}(jQuery);

