        const clock = new THREE.Clock();
        const handleAnimation = () => {
            let puppet = $.CLIENT.portlet.puppet
            if (!puppet.avatar || !puppet.avatar.mixer) { return; }
            puppet.avatar.mixer.update( clock.getDelta() );
        }
        $.CLIENT.portlet.puppet.animation_callbacks.push(handleAnimation)
    }

    let lastAction   = undefined
    let onceAction   = undefined
    let onceCallback = undefined
    let fadeDuration = 0.4;

    const playAction = (name, callback) => {
        let avatar = $.CLIENT.portlet.puppet.avatar
        let action = avatar.actions[name]
        if (lastAction == action){ return; }

        // Stop or Let It Go
        if (lastAction){
            if (lastAction === action){ return; }
            //lastAction.stop()
            lastAction.fadeOut(fadeDuration)
        }

        // Always Play (Once or More)
        if (onceAction){
            if (onceAction === action){ return; }
            stopOnceAction();
        }

        //action.play();
        action.reset().setEffectiveTimeScale( 1 ).fadeIn( fadeDuration ).play();

        // If More store it for later
        if (action.loop !== THREE.LoopOnce){
            lastAction = action
            if (callback) callback();
        } else {
            onceAction   = action
            onceCallback = callback
        }
    }

    const stopOnceAction = () => {
        if (onceAction)  { 
            //onceAction.stop();
            onceAction.reset().setEffectiveTimeScale( 1 ).fadeOut(fadeDuration).play()
            onceAction = undefined; 
        }
        if (onceCallback){ 
            onceCallback(); 
            onceCallback = undefined; 
        }
    }
    const stopAction = (reset) => {
        stopOnceAction();
        if (!reset && lastAction){ 
            //lastAction.play();
            lastAction.reset().setEffectiveTimeScale( 1 ).fadeIn( fadeDuration ).play();

        } else {
            playAction('default');
        }
    }

    const handleAnimate = (msg) => {

        let value = msg.message.event.value
        let cb = () => {  $.CLIENT.sendAck(msg.replyId)  }
        if (!pluginsReady){ return cb(); }
        playAnimation(value, cb);
    }

    // ------------------------------------------
    //  PLUGIN : AVATAR MIXER : PUBLIC PART
    // ------------------------------------------

    const playAnimation = $.CLIENT.portlet.puppet.playAnimation = (value, cb) => {
        if(!value){ if (cb) { cb() }; return; }
        let avatar = $.CLIENT.portlet.puppet.avatar

        // Play a given Animation Track
        if (value.indexOf('Track') == 0){
            playAction(value, cb);
            return;
        }

        // Show/Hide the Avatar
        if (value.indexOf('Action') == 0){
            if (value == 'Action_Hide'){
                avatar.model.scene.visible = false
            }
            if (value == 'Action_Show'){
                avatar.model.scene.visible = true
            }
            if (cb) { cb(); } 
            return;
        }

        // Move to Scene
        if (value.indexOf('Scene') == 0){ 
            let split = value.split('_')
            SCENES[split[1]][split[2]](cb)
            return;
        }

        // Look at location
        if (value.indexOf('Look') == 0){
            let pointer = $.CLIENT.portlet.puppet.avatar.lookAt
            let $canvas = $.CLIENT.portlet.puppet.$canvas

            let target = {x : 0, y : 0}
            let size   = {w : $canvas.width(), h : $canvas.height()}
            if (value == 'Look_Center'){
                target.x= size.w / 2
                target.y= size.h / 2
            }

            pointer.x =   target.x / size.w * 2 - 1
            pointer.y = - target.y / size.h * 2 + 1

            if (cb) { cb(); } 
            return;
        }
    }

    const SCENES = {
        center : {
            data : {
                position : { x : 1, y : 1, z : 1 },
                rotation : { x : 0, y : 0, z : 0 }
            }
        },
        left : {
            data : {
                position: { x: 1, y: 1, z: 1 },
                rotation: { x: 0, y: 0, z: 0}
            }
        },
        zoom : {
            data : {
                position : { x : 1, y : 1, z : 1 },
                rotation : { x : 0, y : 0, z : 0 }
            }
        },
    } 

    const initScenes = (scene) => {
        let _position = (ctx) => { scene.position.set(ctx.x, ctx.y, ctx.z)}
        let _rotation = (ctx) => { scene.rotation.set(ctx.x, ctx.y, ctx.z)}

        SCENES.center.set = (cb) => { _position(SCENES.center.data.position); _rotation(SCENES.center.data.rotation); if (cb){cb()} }
        SCENES.left.set   = (cb) => { _position(SCENES.left.data.position);   _rotation(SCENES.left.data.rotation);   if (cb){cb()} }
        SCENES.zoom.set   = (cb) => { _position(SCENES.zoom.data.position);   _rotation(SCENES.zoom.data.rotation);   if (cb){cb()} }

        // Center => Left
        let cl_walk          = new TWEEN.Tween(SCENES.center.data.position).to(SCENES.left.data.position, 2300).onUpdate(_position).onStart(()   => { playAction('Track_Walk') })
        let cl_rotate        = new TWEEN.Tween(SCENES.center.data.rotation).to(SCENES.left.data.rotation, 300).onUpdate(_rotation).onComplete(() => { stopAction(true)})
        SCENES.center.toLeft = (cb) => { SCENES.center.set(); cl_walk.chain(cl_rotate).start(); if (cb){ setTimeout(cb,3000) } }

        // Center => Zoom
        let cz_walk          = new TWEEN.Tween(SCENES.center.data.position).to(SCENES.zoom.data.position, 1800).onUpdate(_position).onStart(()   => { playAction('Track_Walk') })
        let cz_rotate        = new TWEEN.Tween(SCENES.center.data.rotation).to(SCENES.zoom.data.rotation, 0).onUpdate(_rotation).onComplete(() => { stopAction(true)})
        SCENES.center.toZoom = (cb) => { SCENES.center.set(); cz_walk.chain(cz_rotate).start(); if (cb){ setTimeout(cb,2000) }}


        // Zoom => Left
        let zl_turnback      = new TWEEN.Tween(SCENES.zoom.data.rotation).to({ x: THREE.Math.degToRad(0), y: THREE.Math.degToRad(-160), z: THREE.Math.degToRad(0)}, 500).onUpdate(_rotation).onStart(()   => { playAction('Track_Walk') })
        let zl_walk          = new TWEEN.Tween(SCENES.zoom.data.position).to(SCENES.left.data.position, 1500).onUpdate(_position)
        let zl_rotate        = new TWEEN.Tween(SCENES.zoom.data.rotation).to(SCENES.left.data.rotation, 500).onUpdate(_rotation).onComplete(() => { stopAction(true)})
        SCENES.zoom.toLeft   = (cb) => { SCENES.zoom.set(); zl_turnback.chain(zl_walk.chain(zl_rotate)).start(); if (cb){ setTimeout(cb,2000) }}

    }




