!function ($) {
  $.CLIENT = $.CLIENT || {};
  $.CLIENT.CONFIG =  $.CLIENT.CONFIG || {}
  $.CLIENT.CONFIG.websocket = $.CLIENT.CONFIG.websocket || {}

  // ------------------------------------------
  //  PRIVATE
  // ------------------------------------------

  let socket  = undefined;
  const connect = $.CLIENT.connect = (url, options) => {
    options = $.CLIENT.CONFIG.websocket.options = options || {}

    socket = new WebSocket(url);

    socket.addEventListener("open", (event) => {
      console.log("Websocket", "OPEN", options)
      let data = { 'refresh' : !options.refresh } // Refresh session if it's the first time
      options.refresh = true;
      send('app:ready', data);

    });

    socket.addEventListener("close", (event) => {
      console.log("Websocket", "CLOSE")
      setTimeout(() => { connect(socket.url, $.CLIENT.CONFIG.websocket.options) }, 1000);
    });

    socket.addEventListener("message", function (event) {
      let json = JSON.parse(event.data);
      console.log("Websocket", "MESSAGE", json, event)

      // Split event message in multiple events
      if (json.message && json.message.type === 'event'){
          $.CLIENT.fireEvent(json.message.event.name, json)
      } else if (msg.message) {
          $.CLIENT.fireEvent('message', json)
      }

    });

    socket.addEventListener("error", function (event) {
      console.log("Websocket", "ERROR", event)
    });

  }

  let pingHandle = undefined;
  const ping = function() { send('ws:ping', 'pong', true);  }

  const _send = (data, quiet) => {
    if (!socket) return;

    // Reset the Ping/Pong
    if (pingHandle) { clearTimeout(pingHandle); }
    pingHandle = setTimeout(ping, 45*1000);

    if (!quiet){ $.CLIENT.status($.CLIENT.STATUS.NETWORK, 'Sending...');  }

    // Add a hidden client id provided by browser
    let clientID = $.CLIENT.CONFIG.clientID
    if (clientID) {
        data._client_id  = clientID
    } else { console.error('Missing Client ID'); }

    // Add a hidden hint for custom behavior
    if ($.CLIENT.CONFIG.websocket.options.hint){
      data._client_hint = $.CLIENT.CONFIG.websocket.options.hint
    }

    // Add a cookie secret for security
    if ($.CLIENT.CONFIG.websocket.options.cookie){
      data._client_cookie = $.CLIENT.CONFIG.clientCookie
    }

    // Send Message
    socket.send(JSON.stringify(data));

    if (!quiet){
        $.CLIENT.status($.CLIENT.STATUS.OK,'Sending... done');
    }    
  }

  // ------------------------------------------
  //  PUBLIC
  // ------------------------------------------

  const send = $.CLIENT.send = (type, data, quiet) => {
      _send({ type, data }, quiet)
  }

  const sendAck = $.CLIENT.sendAck = (replyId, data) => {
      if (!replyId){ return; }
      _send({ type: 'ws:ack', replyId, data }, true)
  }

  // ------------------------------------------
  //  REGISTER
  // ------------------------------------------

  const register = () => {
    $.CLIENT.listenEvent('ws:debug', (msg)=> {
      console.log('ws:debug', msg);
      $.CLIENT.sendAck(msg.replyId);
    });
  }

  // Initialization on DOM ready
  $(() => { register(); });

}(jQuery);

