context.cosineSimilarity = (a, b) => {
    const dotProduct = tf.dot(a, b).dataSync()[0];
    const normA = tf.norm(a).dataSync()[0];
    const normB = tf.norm(b).dataSync()[0];
    return dotProduct / (normA * normB);
}

