const extractor = getTextExtractor()
msg.extractor.text = await extractor.extractText({ input: msg.extractor.buffer, type: 'buffer' })

return node.send(msg,false);

const chunkSize    = msg.extractor.chunks.size;
const chunkOverlap = msg.extractor.chunks.overlap;
const chunker = new TextSplitter.RecursiveCharacterTextSplitter({ chunkSize, chunkOverlap });

const cleanString = (text) => {
    text = text.replace(/\\/g, '');
    text = text.replace(/#/g, ' ');
    text = text.replace(/\. \./g, '.');
    text = text.replace(/\s\s+/g, ' ');
    text = text.replace(/(\r\n|\n|\r)/gm, ' ');
    return text.trim();
}

let chunks = await chunker.splitText(cleanString(msg.extractor.text));
msg.extractor.chunks.data = chunks.map((data, index) => {
    const startIndex = index * (chunkSize - chunkOverlap);
    const endIndex = startIndex + data.length;
    return { data, chunkIndex: index, startIndex, endIndex };
});

return node.send(msg, false);

// Build the Document
msg.filename = msg.extractor.filename
msg.payload  = msg.extractor

// Remove Heavy
delete msg.extractor.buffer
delete msg.extractor.text
delete msg.extractor.override

return node.send(msg);

