const showOllama = async (url, name) => {
  return await fetchOllama(url.replace(/chat$/, "show"), {name});
}

const fetchOllama = async (url, post) => {
  const response = await fetch(url, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(post)
  });

  let result = await response.text();
  if (!response.ok) { 
    node.warn({ error: `HTTP ${response.status} - ${response.statusText}`, result})
  }

  try { result = JSON.parse(result) } catch(ex){ node.warn(ex) }
  return result;
}

const handleResults = async (results, options) => {
  let finish_reason  = results.done_reason || 'stop'
  let message = results.message
  message.content = message.content.trim();

  if (message.tool_calls){
    finish_reason = "tool_calls"
    delete message.content
    let uid = Date.now()
    for (let tool of message.tool_calls){
      tool.type = "function"
      tool.id = "call_ollama_" + (uid++);
      //tool['function'].arguments = JSON.stringify(tool['function'].arguments);
    }
  }
  /*
  try { 
    let json = JSON.parse(message.content);
    let fctn = json['function']
    if (json.name && fctn) {

      message.tool_calls[{
        "type"    : "function",
        "id"      : "call_ollama_function",
        "function":  {
          "name"     : json.name,
          "arguments": JSON.stringify(fctn.parameters.properties)
        }
      }]

      delete message.content
      finish_reason = "tool_calls"
      node.warn(message)
    }
  } catch (ex) { node.warn(ex) }
*/

  // Follow OpenAI structure
  results.choices = [{ finish_reason, message }]
}

